import { useState } from "react";

interface UsePaginationProps {
	initialPageIndex?: number;
	initialPageSize?: number;
	totalItems: number;
}

/**
 * Custom hook for handling pagination logic.
 *
 * This hook provides functionality to manage page state based on total items and page size.
 * It allows navigating between pages, jumping to specific pages, and changing the page size.
 *
 * @param {UsePaginationProps} props - Configuration options for pagination.
 * @returns An object containing pagination state and control methods.
 */
const usePagination = ({
	initialPageIndex = 0,
	initialPageSize = 10,
	totalItems,
}: UsePaginationProps) => {
	const [pageIndex, setPageIndex] = useState(initialPageIndex);
	const [pageSize, setPageSize] = useState(initialPageSize);

	// Calculate the total number of pages
	const pageCount = Math.ceil(totalItems / pageSize);

	// Determine if navigation to previous or next page is possible
	const canPreviousPage = pageIndex > 0;
	const canNextPage = pageIndex < pageCount - 1;

	// Navigate to a specific page, ensuring the page number is within bounds
	const gotoPage = (page: number) => {
		setPageIndex(Math.max(0, Math.min(page, pageCount - 1)));
	};

	// Move to the next page if possible
	const nextPage = () => {
		if (canNextPage) {
			setPageIndex(pageIndex + 1);
		}
	};

	// Move to the previous page if possible
	const previousPage = () => {
		if (canPreviousPage) {
			setPageIndex(pageIndex - 1);
		}
	};

	// Change the page size and reset to the first page
	const setPageSizeAndReset = (size: number) => {
		setPageSize(size);
		setPageIndex(0);
	};

	return {
		pageIndex,
		pageSize,
		pageCount,
		canPreviousPage,
		canNextPage,
		gotoPage,
		nextPage,
		previousPage,
		setPageSize: setPageSizeAndReset,
	};
};

export default usePagination;

import { EmployeeFormData } from "@/types/employeeTypes";

/**
 * Filters an array of employees based on a search term.
 *
 * @param {EmployeeFormData[]} employees - The array of employees to filter.
 * @param {string} searchTerm - The term used to filter employees.
 * @returns {EmployeeFormData[]} The filtered array of employees.
 */
export const filterEmployees = (
	employees: EmployeeFormData[],
	searchTerm: string
): EmployeeFormData[] => {
	return employees.filter((employee) =>
		Object.values(employee).some((value) =>
			value.toString().toLowerCase().includes(searchTerm.toLowerCase())
		)
	);
};

/**
 * Sorts an array of employees based on a specified key and direction.
 *
 * @param {EmployeeFormData[]} employees - The array of employees to sort.
 * @param {{ key: keyof EmployeeFormData; direction: string }} sortConfig - Configuration for sorting, including the key to sort by and the direction ('ascending' or 'descending').
 * @returns {EmployeeFormData[]} The sorted array of employees.
 */
export const sortEmployees = (
	employees: EmployeeFormData[],
	sortConfig: { key: keyof EmployeeFormData; direction: string }
): EmployeeFormData[] => {
	return employees.sort((a, b) => {
		if (a[sortConfig.key] < b[sortConfig.key]) {
			return sortConfig.direction === "ascending" ? -1 : 1;
		}
		if (a[sortConfig.key] > b[sortConfig.key]) {
			return sortConfig.direction === "ascending" ? 1 : -1;
		}
		return 0;
	});
};

import { useState, useEffect, useRef } from "react";
import { EmployeeFormData } from "@/types/employeeTypes";
import { filterEmployees, sortEmployees } from "@/utils/filterAndSortEmployees";

/**
 * Custom hook for searching and sorting employee data.
 *
 * This hook manages the state related to search terms and sorting configurations for employee data.
 * It automatically updates the filtered list of employees whenever the search term or sort configuration changes.
 *
 * @param {EmployeeFormData[] | null} employees - The list of employees to be searched and sorted.
 * @returns An object containing search and sort states and functions to manipulate them.
 */
const useSearchAndSort = (employees: EmployeeFormData[] | null) => {
	const [searchTerm, setSearchTerm] = useState("");
	const [filteredEmployees, setFilteredEmployees] = useState<
		EmployeeFormData[]
	>([]);
	const [sortConfig, setSortConfig] = useState<{
		key: keyof EmployeeFormData;
		direction: string;
	}>({ key: "firstName", direction: "ascending" });

	const gotoPageRef = useRef<(pageIndex: number) => void>(() => {});

	useEffect(() => {
		if (employees) {
			// Filter and sort employees based on the current search term and sort configuration
			let filtered = filterEmployees(employees, searchTerm);
			filtered = sortEmployees(filtered, sortConfig);
			setFilteredEmployees(filtered);
			gotoPageRef.current(0); // Reset pagination to the first page after filtering/sorting
		}
	}, [searchTerm, employees, sortConfig]);

	return {
		searchTerm,
		setSearchTerm,
		filteredEmployees,
		sortConfig,
		setSortConfig,
		gotoPageRef,
	};
};

export default useSearchAndSort;

export const inputStyles = {
	base: "flex h-10 w-full rounded-md border border-input px-3 py-2 text-sm file:border-0 file:text-sm file:font-medium focus-visible:outline-none disabled:cursor-not-allowed disabled:opacity-50",
	color: {
		primary:
			"bg-background file:bg-transparent  placeholder:text-muted-foreground",
		error: "border-destructive focus:border-2",
	},
	size: {
		small: "text-sm",
		medium: "text-base",
		large: "text-lg",
	},
};

import React from "react";
import { InputProps } from "@/types/inputTypes";
import { inputStyles } from "@/styles/inputStyles";

/**
 * Input component that can be reused throughout the application.
 *
 * This component is designed to be flexible and customizable, allowing for different colors and sizes.
 * It uses predefined styles from `inputStyles` for consistent styling across the application.
 *
 * @param {InputProps} props - The properties passed to the input component.
 */
const Input: React.FC<InputProps> = React.memo(
	({
		color = "primary",
		size = "medium",
		placeholder,
		fieldError,
		...rest
	}) => {
		// Determine the classes for color and size based on props
		const colorClass = inputStyles.color[color];
		const sizeClass = inputStyles.size[size] || inputStyles.size.medium;
		const baseClass = inputStyles.base;

		return (
			<input
				{...rest}
				className={`${baseClass} ${colorClass} ${sizeClass} dark:[color-scheme:dark]`}
				placeholder={placeholder}
				aria-invalid={!!fieldError}
				required
			/>
		);
	}
);

export default Input;

import { EmployeeFormData } from "@/types/employeeTypes";

/**
 * Requests a sort operation based on a specified key and current sort configuration.
 *
 * @param {keyof EmployeeFormData} key - The key to sort by.
 * @param {{ key: keyof EmployeeFormData; direction: string }} sortConfig - Current sort configuration.
 * @param {React.Dispatch<React.SetStateAction<{ key: keyof EmployeeFormData; direction: string }>>} setSortConfig - Function to set the new sort configuration.
 */
export const requestSort = (
	key: keyof EmployeeFormData,
	sortConfig: { key: keyof EmployeeFormData; direction: string },
	setSortConfig: React.Dispatch<
		React.SetStateAction<{ key: keyof EmployeeFormData; direction: string }>
	>
) => {
	let direction = "ascending";
	if (
		sortConfig &&
		sortConfig.key === key &&
		sortConfig.direction === "ascending"
	) {
		direction = "descending";
	}
	setSortConfig({ key, direction });
};

/**
 * Determines if the sorting is in ascending order based on the given key.
 *
 * @param {keyof EmployeeFormData} key - The key to check.
 * @param {{ key: keyof EmployeeFormData; direction: string }} sortConfig - Current sort configuration.
 * @returns {boolean} True if sorted ascending on the specified key.
 */
export const isSortedAscending = (
	key: keyof EmployeeFormData,
	sortConfig: { key: keyof EmployeeFormData; direction: string }
) => {
	return sortConfig.key === key && sortConfig.direction === "ascending";
};

/**
 * Determines if the sorting is in descending order based on the given key.
 *
 * @param {keyof EmployeeFormData} key - The key to check.
 * @param {{ key: keyof EmployeeFormData; direction: string }} sortConfig - Current sort configuration.
 * @returns {boolean} True if sorted descending on the specified key.
 */
export const isSortedDescending = (
	key: keyof EmployeeFormData,
	sortConfig: { key: keyof EmployeeFormData; direction: string }
) => {
	return sortConfig.key === key && sortConfig.direction === "descending";
};

import React from "react";
import {
	Table,
	TableHeader,
	TableBody,
	TableHead,
	TableRow,
	TableCell,
	Pagination,
	PaginationContent,
	PaginationItem,
	PaginationLink,
	PaginationNext,
	PaginationPrevious,
	EntriesSelector,
} from "opc-ui";

import { EmployeeFormData } from "@/types/employeeTypes";
import usePagination from "@/hooks/usePagination";
import useSearchAndSort from "@/hooks/useSearchAndSort";
import Input from "../atoms/Input";
import { requestSort } from "@/utils/sortUtils";

type Column = {
	key: keyof EmployeeFormData;
	label: string;
};

const columns: Column[] = [
	{ key: "firstName", label: "First Name" },
	{ key: "lastName", label: "Last Name" },
	{ key: "startDate", label: "Start Date" },
	{ key: "department", label: "Department" },
	{ key: "dateOfBirth", label: "Date of Birth" },
	{ key: "street", label: "Street" },
	{ key: "city", label: "City" },
	{ key: "state", label: "State" },
	{ key: "zipCode", label: "Zip Code" },
];

const paginationOptions = [
	{ value: "10", label: "10" },
	{ value: "25", label: "25" },
	{ value: "50", label: "50" },
	{ value: "100", label: "100" },
];

/**
 * EmployeeTable component that displays employee data in a sortable and paginated table.
 *
 * This component integrates search, sorting, and pagination functionalities to manage large sets of employee data.
 * It uses custom hooks for pagination and sorting to provide a dynamic and responsive user experience.
 */
const EmployeeTable: React.FC<{ employees: EmployeeFormData[] | null }> = ({
	employees,
}) => {
	const {
		searchTerm,
		setSearchTerm,
		filteredEmployees,
		sortConfig,
		setSortConfig,
		gotoPageRef,
	} = useSearchAndSort(employees); // Hook to handle search and sorting logic

	const totalItems = filteredEmployees.length; // Total number of items after filtering
	const totalEntries = employees ? employees.length : 0; // Total number of items before filtering
	const {
		pageIndex,
		pageSize,
		pageCount,
		canPreviousPage,
		canNextPage,
		gotoPage,
		nextPage,
		previousPage,
		setPageSize,
	} = usePagination({ totalItems }); // Hook to handle pagination logic

	gotoPageRef.current = gotoPage; // Reference to the gotoPage function for external use

	const currentEmployees = filteredEmployees.slice(
		pageIndex * pageSize,
		(pageIndex + 1) * pageSize
	); // Employees for the current page

	const getSortIcon = (key: keyof EmployeeFormData) => {
		// Function to determine which sort icon to display
		if (!sortConfig) {
			return <span style={{ opacity: 0.3 }}>⇅</span>;
		}
		if (sortConfig.key === key) {
			return sortConfig.direction === "ascending" ? (
				<span>↑</span>
			) : (
				<span>↓</span>
			);
		}
		return <span style={{ opacity: 0.3 }}>⇅</span>;
	};

	return (
		<div>
			<div className='flex items-center justify-between gap-2 mb-4'>
				<div className='flex items-center gap-2'>
					<span className='text-sm text-foreground'>Show</span>
					<EntriesSelector
						value={pageSize.toString()}
						onChange={(e) => setPageSize(Number(e.target.value))}
						options={paginationOptions}
						className='border p-1 rounded outline-none bg-background'
					/>
					<span className='text-sm text-foreground'>entries</span>
				</div>
				<div className='w-1/3'>
					<Input
						type='text'
						placeholder='Search...'
						value={searchTerm}
						onChange={(e) => setSearchTerm(e.target.value)}
					/>{" "}
				</div>
			</div>
			<div className='max-w-4xl mx-auto p-6 bg-muted shadow-lg rounded-lg transition-[background-color] duration-300 ease-in-out'>
				<Table data-testid='employee-table'>
					<TableHeader>
						<TableRow>
							{columns.map((column) => (
								<TableHead
									key={column.key}
									onClick={() =>
										requestSort(column.key, sortConfig, setSortConfig)
									}
									data-testid={`header-${column.key}`}
									className='cursor-pointer '
								>
									{column.label} {getSortIcon(column.key)}
								</TableHead> // Clickable headers for sorting
							))}
						</TableRow>
					</TableHeader>
					<TableBody>
						{currentEmployees.length > 0 ? (
							currentEmployees.map((employee, index) => (
								<TableRow
									key={index}
									data-testid={`employee-row-${index}`}
									className={`${
										index % 2 === 0 ? "bg-background" : "bg-muted"
									} hover:bg-ring`}
									aria-label={`Row ${index + 1}`}
								>
									{columns.map((column) => (
										<TableCell
											key={column.key}
											data-testid={`cell-${column.key}-${index}`}
										>
											{employee[column.key]}
										</TableCell> // Display employee data
									))}
								</TableRow>
							))
						) : (
							<TableRow>
								<TableCell
									colSpan={columns.length}
									data-testid='no-employees-cell'
								>
									No Employees Found
								</TableCell>
							</TableRow>
						)}
					</TableBody>
				</Table>
			</div>
			<div className='flex justify-between items-center mt-1'>
				<span className='text-sm text-foreground  w-full'>
					{`Showing ${pageIndex * pageSize + 1} to ${
						pageIndex * pageSize + currentEmployees.length
					} of ${totalItems} entries`}
					{searchTerm && ` (filtered from ${totalEntries} total entries)`}
				</span>
				<Pagination className='justify-end px-8'>
					<PaginationContent>
						<PaginationItem>
							<PaginationPrevious
								href='#'
								size='null'
								onClick={(e) => {
									e.preventDefault();
									previousPage();
								}}
								isActive={canPreviousPage}
								className={`p-2 ${
									!canPreviousPage && "opacity-50 pointer-events-none"
								}`}
								data-testid='previous-page-button'
							/>
						</PaginationItem>
						{Array.from({ length: pageCount }, (_, i) => (
							<PaginationItem key={i}>
								<PaginationLink
									size='icon'
									href='#'
									isActive={i === pageIndex}
									onClick={(e) => {
										e.preventDefault();
										gotoPage(i);
									}}
								>
									{i + 1}
								</PaginationLink>
							</PaginationItem>
						))}
						<PaginationItem>
							<PaginationNext
								href='#'
								size='null'
								onClick={(e) => {
									e.preventDefault();
									nextPage();
								}}
								isActive={canNextPage}
								className={`p-2 ${
									!canNextPage && "opacity-50 pointer-events-none"
								}`}
								data-testid='next-page-button'
							/>
						</PaginationItem>
					</PaginationContent>
				</Pagination>
			</div>
		</div>
	);
};

export default EmployeeTable;

import { createContext } from "react";
import { EmployeeFormData } from "@/types/employeeTypes";

/**
 * Interface for the properties of the EmployeeContext.
 * @interface
 * @property {EmployeeFormData[]} employees - Array of employee data.
 * @property {(employee: EmployeeFormData) => void} addEmployee - Function to add an employee to the context.
 */
interface EmployeeContextProps {
	employees: EmployeeFormData[];
	addEmployee: (employee: EmployeeFormData) => void;
}

/**
 * Creates a context for managing employee data across the application.
 *
 * This context provides components with access to the list of employees and a method to add new employees.
 * It is intended to be used with a provider that supplies the actual data and implementation of the addEmployee method.
 *
 * @returns {Context<EmployeeContextProps | undefined>} The Employee context with initial state as undefined.
 */
const EmployeeContext = createContext<EmployeeContextProps | undefined>(
	undefined // Initial value set to undefined to indicate no default value
);

export default EmployeeContext;

import React, { useState, useEffect, ReactNode } from "react";
import EmployeeContext from "@/context/EmployeeContext";
import { EmployeeFormData } from "@/types/employeeTypes";

/**
 * Provides the employee context to child components.
 *
 * This provider component manages the employee data using React's Context API. It handles
 * loading employee data from localStorage on initialization, updating localStorage when
 * employees are added, and providing a method to add new employees.
 *
 * @param {ReactNode} children - The child components that will consume the context.
 * @returns {JSX.Element} A context provider wrapping children with employee data and actions.
 */
export const EmployeeProvider: React.FC<{ children: ReactNode }> = ({
	children,
}) => {
	const [employees, setEmployees] = useState<EmployeeFormData[]>(() => {
		// Initialize state with employees from localStorage if available
		const savedEmployees = localStorage.getItem("employees");
		return savedEmployees ? JSON.parse(savedEmployees) : [];
	});

	useEffect(() => {
		// Update localStorage whenever the employees state changes
		localStorage.setItem("employees", JSON.stringify(employees));
	}, [employees]);

	const addEmployee = (employee: EmployeeFormData) => {
		// Add a new employee to the current list of employees
		setEmployees([...employees, employee]);
	};

	return (
		<EmployeeContext.Provider value={{ employees, addEmployee }}>
			{children}
		</EmployeeContext.Provider>
	);
};
