import { createContext } from "react";

/**
 * Interface for the properties of the ThemeContext.
 * Defines the structure of the theme context data.
 * @interface
 * @property {string} theme - Current theme name.
 * @property {() => void} toggleTheme - Function to toggle the theme.
 */
interface ThemeContextType {
	theme: string; // Name of the current theme
	toggleTheme: () => void; // Function to switch themes
}

/**
 * Creates a context for managing theme data across the application.
 *
 * This context provides components with access to the current theme and a method to toggle the theme.
 * It is intended to be used with a provider that supplies the actual data and implementation of the toggleTheme method.
 *
 * @returns {Context<ThemeContextType | undefined>} The Theme context with initial state as undefined.
 */
export const ThemeContext = createContext<ThemeContextType | undefined>(
	undefined // Set to undefined to indicate that there is no default value
);

import React, { useState, ReactNode, useEffect } from "react";
import { ThemeContext } from "@/context/ThemeContext";

interface ThemeProviderProps {
	children: ReactNode;
}

/**
 * Provides the theme context to child components.
 *
 * This provider component manages the theme settings using React's Context API. It handles
 * loading the theme from localStorage on initialization and provides a method to toggle the theme.
 *
 * @param {ThemeProviderProps} props - The props containing children to render within the theme context.
 * @returns {JSX.Element} A context provider wrapping children with theme data and actions.
 */
const ThemeProvider: React.FC<ThemeProviderProps> = ({ children }) => {
	const [theme, setTheme] = useState("light");

	useEffect(() => {
		// Load theme from localStorage and apply it to the document
		const savedTheme = localStorage.getItem("theme");
		if (savedTheme) {
			setTheme(savedTheme);
			document.documentElement.classList.add(savedTheme);
		}
	}, []);

	const toggleTheme = () => {
		// Toggle between 'light' and 'dark' themes
		const newTheme = theme === "light" ? "dark" : "light";
		setTheme(newTheme);
		localStorage.setItem("theme", newTheme);
		document.documentElement.classList.remove(theme);
		document.documentElement.classList.add(newTheme);
	};

	return (
		<ThemeContext.Provider value={{ theme, toggleTheme }}>
			{children}
		</ThemeContext.Provider>
	);
};

export default ThemeProvider;

import React from "react";
import { Header, Footer } from "@/index";

/**
 * Interface for GeneralTemplate props.
 * @interface
 * @property {React.ReactNode} children - The content to be displayed between the header and footer.
 */
interface GeneralTemplateProps {
	children: React.ReactNode;
}

/**
 * Represents a general layout template for the application.
 *
 * This component wraps the main content with a Header and a Footer. It is designed to be used
 * as a layout template for pages that require a consistent header and footer.
 *
 * @component
 * @param {GeneralTemplateProps} props - The props containing children to render within the layout.
 * @example
 * return (
 *   <GeneralTemplate>
 *     <div>Page Content Here</div>
 *   </GeneralTemplate>
 * )
 */
const GeneralTemplate: React.FC<GeneralTemplateProps> = React.memo(
	({ children }) => {
		return (
			<div className='flex flex-col min-h-screen bg-background transition-[background-color] duration-300 ease-in-out'>
				<Header />
				<main className='mx-auto p-6 flex-grow flex justify-center'>
					{children}
				</main>
				<Footer />
			</div>
		);
	}
);

export default GeneralTemplate;

