import React from "react";
import { InputProps } from "@/types/inputTypes";
import { inputStyles } from "@/styles/inputStyles";

/**
 * Input component that can be reused throughout the application.
 *
 * This component is designed to be flexible and customizable, allowing for different colors and sizes.
 * It uses predefined styles from `inputStyles` for consistent styling across the application.
 *
 * @param {InputProps} props - The properties passed to the input component.
 */
const Input: React.FC<InputProps> = React.memo(
	({
		color = "primary",
		size = "medium",
		placeholder,
		fieldError,
		...rest
	}) => {
		// Determine the classes for color and size based on props
		const colorClass = inputStyles.color[color];
		const sizeClass = inputStyles.size[size] || inputStyles.size.medium;
		const baseClass = inputStyles.base;

		return (
			<input
				{...rest}
				className={`${baseClass} ${colorClass} ${sizeClass} dark:[color-scheme:dark]`}
				placeholder={placeholder}
				aria-invalid={!!fieldError}
				required
			/>
		);
	}
);

export default Input;

import React, { LabelHTMLAttributes } from "react";

/**
 * Label component that can be reused throughout the application.
 *
 * This component is designed to be flexible and can be styled externally via `className`.
 * It extends standard HTML label attributes for enhanced accessibility and functionality.
 *
 * @param {LabelProps} props - The properties passed to the label component.
 */
interface LabelProps extends LabelHTMLAttributes<HTMLLabelElement> {
	children: React.ReactNode;
}

const Label: React.FC<LabelProps> = ({ children, className, ...rest }) => {
	// Apply default and additional classes for styling
	const baseClass = "block mb-1";
	return (
		<label {...rest} className={`${baseClass} ${className}`}>
			{children}
		</label>
	);
};

export default Label;

import { useContext } from "react";
import { ThemeContext } from "../context/ThemeContext";

/**
 * Custom hook for accessing theme context.
 *
 * This hook simplifies the process of using the theme context within components.
 * It ensures that the theme context is used within a ThemeProvider to avoid errors.
 *
 * @returns The theme context object containing the current theme and a function to toggle the theme.
 * @throws Error if the hook is used outside of a ThemeProvider.
 */
export const useTheme = () => {
	const context = useContext(ThemeContext);
	if (!context) {
		// Ensures the hook is used within a ThemeProvider
		throw new Error("useTheme must be used within a ThemeProvider");
	}
	return context;
};

import React from "react";
import { useTheme } from "@/hooks/useTheme";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun, faMoon } from "@fortawesome/free-solid-svg-icons";

/**
 * ThemeToggle component that allows users to switch between light and dark themes.
 *
 * This component uses the `useTheme` hook to read and set the current theme.
 * It visually represents the current theme with a toggle switch that moves horizontally and changes icons.
 */
const ThemeToggle: React.FC = React.memo(() => {
	const { theme, toggleTheme } = useTheme();

	// Function to handle keyboard events
	const handleKeyPress = (event: React.KeyboardEvent) => {
		if (event.key === "Enter" || event.key === " ") {
			toggleTheme();
		}
	};

	return (
		<div className='flex items-center'>
			<button
				onClick={toggleTheme}
				onKeyDown={handleKeyPress} // Handle keyboard events
				className={`relative inline-flex items-center h-8 w-16 rounded-full p-1 transition-colors duration-300 ease-in-out hover:bg-opacity-75 focus:outline-none bg-accent`}
				data-testid='theme-toggle'
				aria-label='Toggle theme' // ARIA label for accessibility
				aria-checked={theme === "dark"} // ARIA state for accessibility
				role='switch' // ARIA role for accessibility
			>
				<span
					className={`absolute inset-y-1 left-0 flex items-center justify-center w-6 h-6 transform rounded-full shadow-md transition-transform duration-300 ease-in-out ${
						theme === "dark" ? "translate-x-9" : "translate-x-1"
					}`}
				>
					<FontAwesomeIcon
						icon={theme === "dark" ? faMoon : faSun}
						className={`text-yellow-500 transition-transform duration-300 ease-in-out ${
							theme === "dark" ? "rotate-360" : "-rotate-180"
						}`}
					/>
				</span>
			</button>
		</div>
	);
});

export default ThemeToggle;

import React, { useState, useEffect } from "react";
import { FormFieldProps } from "@/types/formFieldTypes";
import { Input, Label } from "@/index";

/**
 * FormField component that dynamically renders an input element based on provided props.
 *
 * This component handles input changes, validation, and displays errors. It supports text inputs.
 *
 * @param {FormFieldProps} props - The properties passed to the form field component.
 */
const FormField: React.FC<FormFieldProps> = ({
	label,
	id,
	type = "text",
	error,
	className,
	value,
	name,
	rules,
	onChange,
	color = "primary",
	size = "medium",
	...rest
}) => {
	const [fieldError, setFieldError] = useState<string | undefined>(error);
	const [inputType, setInputType] = useState<string>(
		type === "date" ? "text" : type
	);

	useEffect(() => {
		setFieldError(error);
	}, [error]);

	// Validates the field value against the provided rules and sets the appropriate error message.
	const validateField = (value: string, name: string) => {
		if (rules?.required && !value) {
			return rules.required;
		} else if (
			rules?.pattern &&
			value &&
			!new RegExp(rules.pattern.value).test(value.toString())
		) {
			return rules.pattern.message;
		} else if (name === "dateOfBirth" && new Date(value) > new Date()) {
			return "Date of birth cannot be in the future";
		} else if (name === "startDate" && new Date(value) < new Date()) {
			return "Start date cannot be in the past";
		}
		return undefined;
	};

	// Handles field blur event to validate and potentially reset input types.
	const handleBlur = () => {
		if (name) {
			const error = validateField(value?.toString() || "", name);
			setFieldError(error);
		}
		if (type === "date") {
			setInputType("text");
		}
	};

	// Handles changes to the input value, validates the field, and calls the onChange prop.
	const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		onChange?.(e.target.value);
		const newValue = e.target.value;
		if (name) {
			const error = validateField(newValue, name);
			setFieldError(error);
		}
	};

	return (
		<div className='flex flex-col'>
			<div className={`${className}`}>
				<Label
					htmlFor={id}
					className={`transition-all duration-200 ease-in-out ${
						fieldError ? "text-destructive" : ""
					}`}
				>
					{label}
				</Label>
				<Input
					id={id}
					type={inputType}
					value={value}
					name={name}
					onBlur={handleBlur}
					onChange={handleChange}
					aria-invalid={!!fieldError}
					fieldError={fieldError}
					color={fieldError ? "error" : color}
					size={size}
					{...rest}
				/>
			</div>
			{fieldError && (
				<p className='text-destructive text-sm mt-1'>{fieldError}</p>
			)}
		</div>
	);
};

export default FormField;

import React from "react";

