/**
 * Configuration for field validation rules.
 *
 * This module defines validation rules for various fields used in forms throughout the application.
 * Each field has rules for required validation and, optionally, pattern matching.
 */
interface ValidationRule {
	required?: string;
	pattern?: {
		value: RegExp;
		message: string;
	};
	validate?: {
		[key: string]: (value: string) => boolean | string;
	};
}

export const fieldRules: { [key: string]: ValidationRule } = {
	firstName: {
		required: "First name is required",
		pattern: {
			value: /^[a-zA-Z]+$/, // Regex: Matches only alphabetic characters (no spaces or numbers)
			message: "First name can only contain letters",
		},
	},
	lastName: {
		required: "Last name is required",
		pattern: {
			value: /^[a-zA-Z]+$/, // Regex: Matches only alphabetic characters (no spaces or numbers)
			message: "Last name can only contain letters",
		},
	},
	dateOfBirth: {
		required: "Date of birth is required",
		pattern: {
			value: /^\d{2}\/\d{2}\/\d{4}$/, // Regex: Matches date format MM/DD/YYYY
			message: "Date of birth must be in the format MM/DD/YYYY",
		},
		validate: {
			notInFuture: (value) => {
				const today = new Date();
				const [month, day, year] = value.split("/").map(Number);
				const date = new Date(year, month - 1, day);
				return date <= today || "Date of birth cannot be in the future";
			},
		},
	},
	startDate: {
		required: "Start date is required",
		pattern: {
			value: /^\d{2}\/\d{2}\/\d{4}$/, // Regex: Matches date format MM/DD/YYYY
			message: "Start date must be in the format MM/DD/YYYY",
		},
	},
	department: {
		required: "Department is required",
	},
	street: {
		required: "Street is required",
		pattern: {
			value: /^[a-zA-Z0-9\s]+$/, // Regex: Matches letters, numbers, and spaces
			message: "Street can only contain letters, numbers and spaces",
		},
	},
	city: {
		required: "City is required",
		pattern: {
			value: /^[a-zA-Z\s]+$/, // Regex: Matches letters and spaces only
			message: "City can only contain letters and spaces",
		},
	},
	state: {
		required: "State is required",
	},
	zipCode: {
		required: "Zip code is required",
		pattern: {
			value: /^\d{5}(-\d{4})?$/, // Regex: Matches a 5-digit number or a 9-digit format XXXXX-XXXX
			message:
				"Zip code must be a 5-digit number or a 9-digit format XXXXX-XXXX",
		},
	},
};

import { EmployeeFormData } from "@/types/employeeTypes";
import { fieldRules } from "@/config/fieldRules";

/**
 * Validates form data against predefined field rules.
 *
 * This function checks each field in the form data against the corresponding rules defined in `fieldRules`.
 * It returns an object containing error messages for fields that do not comply with the rules.
 *
 * @param {EmployeeFormData} formData - The form data to validate.
 * @returns {Partial<EmployeeFormData>} An object with error messages for each field that fails validation.
 */
export function validate(
	formData: EmployeeFormData
): Partial<EmployeeFormData> {
	const errors: Partial<EmployeeFormData> = {};

	// Iterate over each field rule and apply validation
	Object.keys(fieldRules).forEach((key) => {
		const value = formData[key as keyof EmployeeFormData];
		const rules = fieldRules[key as keyof typeof fieldRules];

		switch (true) {
			// Check if the field is required and not filled
			case rules.required && !value:
				errors[key as keyof EmployeeFormData] = rules.required;
				break;

			// Check if the field has a pattern rule and fails the pattern validation
			case rules.pattern !== undefined &&
				value &&
				!rules.pattern.value.test(value):
				errors[key as keyof EmployeeFormData] = rules.pattern.message;
				break;

			// Check if the field has custom validation rules
			case rules.validate !== undefined:
				Object.keys(rules.validate).forEach((validateKey) => {
					const validationFn = rules.validate![validateKey];
					const validationResult = validationFn(value);
					if (typeof validationResult === "string") {
						errors[key as keyof EmployeeFormData] = validationResult;
					}
				});
				break;
		}
	});

	return errors;
}

import { createContext } from "react";
import { EmployeeFormData } from "@/types/employeeTypes";

/**
 * Interface for the properties of the EmployeeContext.
 * @interface
 * @property {EmployeeFormData[]} employees - Array of employee data.
 * @property {(employee: EmployeeFormData) => void} addEmployee - Function to add an employee to the context.
 */
interface EmployeeContextProps {
	employees: EmployeeFormData[];
	addEmployee: (employee: EmployeeFormData) => void;
}

/**
 * Creates a context for managing employee data across the application.
 *
 * This context provides components with access to the list of employees and a method to add new employees.
 * It is intended to be used with a provider that supplies the actual data and implementation of the addEmployee method.
 *
 * @returns {Context<EmployeeContextProps | undefined>} The Employee context with initial state as undefined.
 */
const EmployeeContext = createContext<EmployeeContextProps | undefined>(
	undefined // Initial value set to undefined to indicate no default value
);

export default EmployeeContext;

import React, { useState, useContext } from "react";
import { FormField, Button, Label } from "@/index";
import { validate } from "@/utils/formValidation";
import { fieldRules } from "@/config/fieldRules";
import { departmentOptions } from "@/utils/departmentOptions";
import { states } from "@/utils/states";
import EmployeeContext from "@/context/EmployeeContext";
import { EmployeeFormData } from "@/types/employeeTypes";
import { DatePicker, Combobox, useToast } from "opc-ui";

/**
 * EmployeeForm component for creating or editing employee records.
 *
 * This form includes fields for personal details, address, and employment details.
 * It validates inputs and displays errors where necessary using a custom validation logic.
 */
const EmployeeForm: React.FC = () => {
	const context = useContext(EmployeeContext);
	if (!context) {
		throw new Error("EmployeeForm must be used within an EmployeeProvider");
	}
	const { addEmployee } = context;
	const { toast } = useToast();

	const [formData, setFormData] = useState<EmployeeFormData>({
		firstName: "",
		lastName: "",
		dateOfBirth: "",
		startDate: "",
		street: "",
		city: "",
		state: "",
		zipCode: "",
		department: "",
	});

	const [errors, setErrors] = useState<Partial<EmployeeFormData>>({});

	// Handles changes in form fields and updates state
	const handleChange = (value: string, name: string) => {
		setFormData((prevFormData) => ({
			...prevFormData,
			[name]: value,
		}));
		setErrors((prevErrors) => ({
			...prevErrors,
			[name]: "",
		}));
	};

	// Submits the form data after validation
	const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
		e.preventDefault();
		const newErrors = validate(formData);
		if (Object.keys(newErrors).length > 0) {
			setErrors(newErrors);
			toast({
				title: "Error : Please fill in all fields correctly.",
				variant: "destructive",
			});
			console.log("Error: Please fill in all fields correctly.");
		} else {
			addEmployee(formData);
			toast({ title: "Employee created successfully!" });
			console.log("Employee Validated");
		}
	};

	// Clears all form fields
	const handleClear = () => {
		setFormData({
			firstName: "",
			lastName: "",
			dateOfBirth: "",
			startDate: "",
			street: "",
			city: "",
			state: "",
			zipCode: "",
			department: "",
		});
		setErrors({});
	};

	const addressFields = [
		{
			label: "Street",
			id: "street",
			name: "street",
			placeholder: "123 Main St",
			rules: fieldRules.street,
		},
		{
			label: "City",
			id: "city",
			name: "city",
			placeholder: "Anytown",
			rules: fieldRules.city,
		},
		{
			label: "Zip Code",
			id: "zipCode",
			name: "zipCode",
			placeholder: "12345",
			rules: fieldRules.zipCode,
		},
	];

	return (
		<div className='max-w-4xl mx-auto p-6 bg-secondary shadow-lg rounded-lg transition-[background-color] duration-300 ease-in-out'>
			<form
				onSubmit={handleSubmit}
				className='space-y-6'
				noValidate
				data-testid='employee-form'
			>
				<div className='grid grid-cols-1 md:grid-cols-3 gap-4'>
					<FormField
						aria-label='First Name'
						label='First Name'
						id='firstName'
						type='text'
						value={formData.firstName}
						onChange={(value: string) => handleChange(value, "firstName")}
						name='firstName'
						rules={fieldRules.firstName}
						error={errors.firstName}
						placeholder='John'
						data-testid='first-name-input'
					/>
					<FormField
						aria-label='Last Name'
						label='Last Name'
